import { Component, OnInit } from '@angular/core';
import quizz_questions from'../../../assets/data/quizz_questions.json'

@Component({
  selector: 'app-quizz',
  templateUrl: './quizz.component.html',
  styleUrls: ['./quizz.component.css']
})
export class QuizzComponent implements OnInit {

  title:string = ""

  questions:any
  questionSelected:any

  answers:string[] = []
  answerSelected:string =""

  questionIndex:number = 0
  questionMaxIndex:number = 0

  finished:boolean = false

  constructor() {}

  ngOnInit(): void {
    if(quizz_questions){
      this.finished = false
      this.title = quizz_questions.title

      this.questions = quizz_questions.questions
      this.questionSelected = this.questions[this.questionIndex]

      this.questionIndex = 0
      this.questionMaxIndex = this.questions.length

      console.log(this.questionIndex)
      console.log(this.questionMaxIndex)
    }
  }

  playerChoose(value:string) {
    this.answers.push(value)
    this.nextStep()
  }

  async nextStep() {
    this.questionIndex+=1

    if(this.questionMaxIndex > this.questionIndex){
      this.questionSelected = this.questions[this.questionIndex]
    }
    // else{
    //   const finalAnswer:string = await this.checkResult(this.answers)
    //   this.finished = true
    //   this.answerSelected = quizz_questions.results[finalAnswer as keyof typeof quizz_questions.results]
    //   //verificar opção ganhadora
    // }
  }

  async checkResult(answers:string[]){
    const result = answers.reduce((previous, current, i, arr)=>{
      if(
        arr.filter(item => item === previous).length >
        arr.filter(item => item === current).length
      ){
        return previous
      }else{
        return current
      }
    })
  }
}
<div class="quiz__container">
    <div class="quizz_logo">
        <img src="assets/imgs/logo.png" alt="">
        <h3>{{title}}</h3>
    </div>

    <div class="quizz_questions" *>
        <h3>{{questionSelected.question}}</h3>
    </div>

    <div class="quizz_options">

        <button
            *ngFor="let option of questionSelected.oprtions"
            (click)="playerChoose(option.alias)"
            class="btn-option"
        >
            {{option.name}}
        </button>

    </div>

    <div *ngIf=finished class="quizz_results">
        <h1>O seu resultado é: </h1>
        <p>{{answerSelected}}</p>
    </div>
</div>
